import React, { Component } from "react";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import ChartPieUsine from "./components/charts/ChartPieUsine";

const zones = [
  {
    name: "Quai EP",
    latitude: 32.305572,
    longitude: -9.247014
  },
  {
    name: "Zone engrai/phosphate",
    latitude: 32.308801,
    longitude: -9.245253
  },

  {
    name: "Zone soufre",
    latitude: 32.308801,
    longitude: -9.245253
  },
  {
    name: "ANP",
    latitude: 32.308801,
    longitude: -9.245253
  },
  {
    name: "Zone amoniac",
    latitude: 32.308801,
    longitude: -9.245253
  }
];

// const CitiInfo = styled.div`
//   height: 300px;
//   width: 300px;
//   display: flex;
//   align-items: center;
//   justify-content: center;
//   color: ${props => props.color};
// `;

const bounds = [
  [32.1, -12],
  [30, -4]
];

export default class SimpleExample extends Component {
  render() {
    return (
      <Map
        bounds={bounds}
        boundsOptions={{
          padding: [40, 40]
        }}
        style={{
          height: 800,
          width: "100%"
        }}
      >
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {zones.map(city => (
          <Marker key={city.name} position={[city.latitude, city.longitude]}>
            <Popup closeButton={true}>
              <ChartPieUsine />
            </Popup>
          </Marker>
        ))}
      </Map>
    );
  }
}
