import React from "react";
import ReactMapGL, { Marker, Popup, NavigationControl } from "react-map-gl";
import styled from "styled-components";

const TOKEN =
  "pk.eyJ1IjoiaHVjaGVuc2NiIiwiYSI6ImNqdHpnbGZ5ejFneXEzeW81a3B3anJkZGoifQ.RjMCIQBbS0dlzTl85EogQw";

const CitiMarker = styled.div`
  background-color: ${props => props.color};
  width: 20px;
  height: 20px;
  border-radius: 20px;
  opacity: 0.7;
  transition: opacity 0.3s;
  cursor: pointer;

  :hover {
    opacity: 0.9;
  }
`;

const CitiInfo = styled.div`
  height: 100px;
  width: 100px;
  background: yellow;
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${props => props.color};
`;

const cities = [
  {
    name: "SG",
    latitude: 1.3521,
    longitude: 103.8198,
    color: "red",
  },
  {
    name: "KL",
    latitude: 3.139,
    longitude: 101.6869,
    color: "blue",
  },
];

const center = {
  latitude:
    (Math.min(...cities.map(c => c.latitude)) +
      Math.max(...cities.map(c => c.latitude))) /
    2,
  longitude:
    (Math.min(...cities.map(c => c.longitude)) +
      Math.max(...cities.map(c => c.longitude))) /
    2,
};

export default class Map extends React.Component {
  state = {
    viewport: {
      latitude: center.latitude,
      longitude: center.longitude,
      zoom: 6,
    },
    popupInfo: null,
  };

  updateViewport = viewport => {
    this.setState({ viewport });
  };

  render() {
    return (
      <ReactMapGL
        {...this.state.viewport}
        width="100%"
        height={400}
        onViewportChange={this.updateViewport}
        mapboxApiAccessToken={TOKEN}
      >
        {cities.map(c => (
          <Marker
            key={c.name}
            latitude={c.latitude}
            longitude={c.longitude}
            offsetLeft={-10}
            offsetTop={-10}
          >
            <CitiMarker
              color={c.color}
              onMouseEnter={() => {
                this.setState({
                  popupInfo: c,
                });
              }}
            />
          </Marker>
        ))}

        {this.state.popupInfo && (
          <Popup
            anchor="left"
            longitude={this.state.popupInfo.longitude}
            latitude={this.state.popupInfo.latitude}
            closeButton={false}
          >
            <CitiInfo color={this.state.popupInfo.color}>
              {this.state.popupInfo.name}
            </CitiInfo>
          </Popup>
        )}

        <div style={{ position: "absolute", left: 10, top: 10 }}>
          <NavigationControl
            showCompass={false}
            onViewportChange={this.updateViewport}
          />
        </div>
      </ReactMapGL>
    );
  }
}
